import { createReducer } from "@reduxjs/toolkit";

const initialState = {
  c: 0,
};
export const customReducer = createReducer(initialState, {
  increment: (state, action) => {
    state.c = state + 1;
  },

  incrementbyValue: (state, action) => {
    // state.c=state+1;
    state.c = state + action.payload;
  },

  decrement: (state, action) => {
    state.c = state - 1;
  },
});
